import React, { useState } from 'react';

function InterestCalculator() {
    const [principal, setPrincipal] = useState('');
    const [rate, setRate] = useState('');
    const [time, setTime] = useState('');
    const [interest, setInterest] = useState(null);

    const calcInterest = () => {
        let p = parseFloat(principal);
        let r = parseFloat(rate);
        let t = parseFloat(time);

        if (isNaN(p) || isNaN(r) || isNaN(t)) {
            setInterest("Please enter valid numbers");
            return;
        }

        let si = (p * r * t) / 100; // Simple Interest formula
        setInterest(si.toFixed(2)); // Round to 2 decimal places
    };

    return (
        <div style={styles.container}>
            <h2>Simple Interest Calculator</h2>

            <label>Principal (P):</label>
            <input
                type="number"
                value={principal}
                onChange={(e) => setPrincipal(e.target.value)}
                style={styles.input}
                placeholder="Enter principal amount"
            />

            <label>Rate of Interest (R% per year):</label>
            <input
                type="number"
                value={rate}
                onChange={(e) => setRate(e.target.value)}
                style={styles.input}
                placeholder="Enter rate of interest"
            />

            <label>Time (T in years):</label>
            <input
                type="number"
                value={time}
                onChange={(e) => setTime(e.target.value)}
                style={styles.input}
                placeholder="Enter time in years"
            />

            <button onClick={calcInterest} style={styles.button}>Calculate Interest</button>

            {interest !== null && (
                <h3>Interest: {interest}</h3>
            )}
        </div>
    );
}

// CSS styles
const styles = {
    container: {
        textAlign: "center",
        maxWidth: "400px",
        margin: "auto",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "10px",
        backgroundColor: "#f9f9f9",
        boxShadow: "0px 0px 10px rgba(0,0,0,0.1)"
    },
    input: {
        display: "block",
        width: "90%",
        margin: "10px auto",
        padding: "8px",
        fontSize: "16px"
    },
    button: {
        padding: "10px 20px",
        fontSize: "16px",
        backgroundColor: "#007BFF",
        color: "white",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer"
    }
};

export default InterestCalculator;
